package proassign9;

public class sami {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		char b='a';
		for (int i=1;i<=a;i++)
		{
			System.out.print(i);
			System.out.print(b);
			
			if(i<a)
			{
				System.out.print(",");
			}
			b++;
		}
		
		

	}

}
